import './ExpenceItem.css'; //* Simply to tell JS we going to use that particular styles with it!

function ExpenceItem(props) {
	const months = props.date.toLocaleString('ru-RU', { month: 'long' });
	const day = props.date.toLocaleString('ru-RU', { day: '2-digit' });
	const year = props.date.getFullYear();

	return (
		<div className="expense-item">
			<div>
				<div>{months}</div>
				<div>{year}</div>
				<div>{day}</div>
			</div>
			<div className="expense-item__description">
				<h2>{props.title}</h2>
				<div className="expense-item__price">${props.amount.toFixed(2)}</div>
			</div>
		</div>
	);
}

export default ExpenceItem;
