import React from 'react';
import ExpenseItem from './ExpenseItem';
import './ExpenseRender.css';
import './ExpensesFilter';
import Card from '../UI/Card';
import ExpensesFilter from './ExpensesFilter';
import { useState } from 'react';

function ExpenseRender(props) {
	const [filteredYear, setFilteredYear] = useState('2020'); //* We initiallized a year

	const filterChangeHandler = (selectedYear) => {
		setFilteredYear(selectedYear);
	};

	const filteredExpenses = props.items.filter((item) => {
		return item.date.getFullYear() === +filteredYear;
	}); //We got an Array
	const label = <h2>Sultan's cars:</h2>;
	let expenseContent = <p>Нет приобретенных машин</p>;

	if (filteredExpenses.length > 0) {
		expenseContent = filteredExpenses.map((expenses) => {
			expenses.date.getFullYear();
			return (
				<ExpenseItem
					key={expenses.id}
					title={expenses.title}
					amount={expenses.amount}
					date={expenses.date}
				/>
			);
		});
	}
	return (
		<div>
			<Card className="expenses">
				<ExpensesFilter
					currentYear={filteredYear}
					onChangeFilter={filterChangeHandler}
				/>
				<h2>Sultan's cars:</h2>
				{expenseContent}
			</Card>
		</div>
	);
}

export default ExpenseRender;
