import React from 'react';
import ExpenseItem from './ExpenseItem';
import './ExpenseRender.css';
import './ExpensesFilter';
import Card from '../UI/Card';
import ExpensesFilter from './ExpensesFilter';
import { useState } from 'react/cjs/react.production.min';

function ExpenseRender(props) {
	const [filteredYear, setFilteredYear] = useState('2020'); //* We initiallized a year

	const filterChangeHandler = (exp) => {
		console.log(exp);
	};

	return (
		<div>
			<Card className="expenses">
				<ExpensesFilter onChangeFilter={filterChangeHandler} />
				<h2>Sultan's cars:</h2>
				<div>
					<ExpenseItem
						date={props.items.at(0).date} //* props.expenses
						title={props.items.at(0).title}
						amount={props.items.at(0).amount}
					/>
				</div>
				<div>
					<ExpenseItem
						date={props.items.at(1).date}
						title={props.items.at(1).title}
						amount={props.items.at(1).amount}
					/>
				</div>
				<div>
					<ExpenseItem
						date={props.items.at(2).date}
						title={props.items.at(2).title}
						amount={props.items.at(2).amount}
					/>
				</div>
				<div>
					<ExpenseItem
						date={props.items.at(3).date}
						title={props.items.at(3).title}
						amount={props.items.at(3).amount}
					/>
				</div>
			</Card>
		</div>
	);
}

export default ExpenseRender;

