import React, { useState } from 'react';
import ExpensesForm from './ExpensesForm';
import './NewExpenses.css';
import './CloseForm';
import './OpenForm';
import OpenForm from './OpenForm';

function NewExpenses(props) {
	const saveExpenseComponentData = (enteredExpenseData) => {
		const expenseData = {
			...enteredExpenseData,
			id: Math.random(),
		};
		props.onAddExpense(expenseData);
	};

	const [btnOpen, setButtonOpen] = useState(false);

	const btnOpenHandler = () => {
		setButtonOpen(true);
	};

	// let showForm = <button onClick={btnOpenHandler}>Add new car</button>;

	function showForm(btnOpenHandler) {
		return <button onClick={btnOpenHandler}>Add new car</button>;
	}

	if (btnOpen) {
		showForm = (
			<ExpensesForm
				showForm={showForm}
				onSaveExpenseData={saveExpenseComponentData}
			/>
		);
	}

	return <div className="new-expense">{showForm(btnOpenHandler)}</div>;
}

export default NewExpenses;
