import React, { useState } from 'react';
import './ExpensesFilter.css';

function ExpensesFilter(props) {
	const [year, setYear] = useState([2022, 2021, 2020, 2019]);

	const changeYearHandler = (e) => {
		setYear(e.target.value);
		console.log(e.target.value);
	};

	return (
		<div className="expenses-filter">
			<div className="expenses-filter__control">
				<label className="label">Filter by year</label>

				<select className="select" onChange={changeYearHandler}>
					<option value={year[0]}>2022</option>
					<option value={year[1]}>2021</option>
					<option value={year[2]}>2020</option>
					<option value={year[3]}>2019</option>
				</select>
			</div>
		</div>
	);
}

export default ExpensesFilter;
