import React from 'react';
import ExpenseItem from './ExpenseItem';
import './ExpenseRender.css';
import './ExpensesFilter';
import Card from '../UI/Card';
import ExpensesFilter from './ExpensesFilter';
import { useState } from 'react';

function ExpenseRender(props) {
	const [filteredYear, setFilteredYear] = useState('2020'); //* We initiallized a year

	const filterChangeHandler = (selectedYear) => {
		setFilteredYear(selectedYear);
	};

	const getYear = (gotIt) => {
		// console.log(`Got it: ${gotIt}`);
		return gotIt;
	};
	const iti = props.years.filter((exp) => {
		console.log(exp);
		return exp === getYear();
	});
	console.log(iti);

	return (
		<div>
			<Card className="expenses">
				<ExpensesFilter
					currentYear={filteredYear}
					onChangeFilter={filterChangeHandler}
					year={getYear}
				/>
				<h2>Sultan's cars:</h2>
				{props.items.map((expenses) => (
					<ExpenseItem
						key={expenses.id}
						title={expenses.title}
						amount={expenses.amount}
						date={expenses.date}
					/>
				))}
			</Card>
		</div>
	);
}

export default ExpenseRender;
