import React, { useState } from 'react';
import ExpensesForm from './ExpensesForm';
import './NewExpenses.css';
import './CloseForm';
import './OpenForm';
import OpenForm from './OpenForm';

function NewExpenses(props) {
	const saveExpenseComponentData = (enteredExpenseData) => {
		const expenseData = {
			...enteredExpenseData,
			id: Math.random(),
		};
		props.onAddExpense(expenseData);
	};

	const [btnOpened, setButtonOpen] = useState(false);

	const clickHandler = () => {
		setButtonOpen(true);
	};

	let showForm = '';

	if (btnOpened) {
		showForm = (
			<div>
				{' '}
				<button onClick={clickHandler}>Add new car</button>
				<ExpensesForm onSaveExpenseData={saveExpenseComponentData} />
				{showForm}
			</div>
		);
	}

	return <div className="new-expense"></div>;
}

export default NewExpenses;
