import React, { useState } from 'react';
import './ExpensesFilter.css';

function ExpensesFilter() {
	const [year, setYear] = useState([2019, 2020, 2021, 2022]);

	const changeYearHandler = (e) => {
		console.log(e);
		setYear(e.target.value);
	};

	return (
		<div className="expenses-filter">
			<div className="expenses-filter__control">
				<label className="label">Filter by year</label>

				<select className="select">
					<option value={year.at(3)}>2022</option>
					<option value={year.at(2)}>2021</option>
					<option value={year.at(1)}>2020</option>
					<option value={year.at(0)}>2019</option>
				</select>
			</div>
		</div>
	);
}

export default ExpensesFilter;
