import React from 'react'; //* Default component
import { useState } from 'react'; //* Component of React library, that helps us with Event Handler!
import './ExpenseItem.css'; //* Simply to tell JS we going to use that particular styles with it!
import ExpenseDate from './ExpenseDate';
import Card from '../UI/Card';

function ExpenceItem(props) {
	const [car, upgradeCar] = useState(props.title);
	const [price, setPrice] = useState(props.amount.toFixed(3));

	const clickBtnHandler = () => {
		upgradeCar('McLaren 720S Spayder');
		setPrice(890.0);
		console.log(
			`Cars have been changed, from ${car} to ${upgradeCar().innerText()}. Also price has been changed to ${setPrice().innerText()}`
		);
	};

	return (
		<Card className="expense-item">
			<ExpenseDate date={props.date} />
			<div className="expense-item__description">
				<h2>{car}</h2>
				<div className="expense-item__price">${price}</div>
			</div>
			<button onClick={clickBtnHandler}>Change title</button>
		</Card>
	);
}

export default ExpenceItem;
