import React, { useState } from 'react';
import ExpensesForm from './ExpensesForm';
import './NewExpenses.css';
import './CloseForm';
import './OpenForm';
import OpenForm from './OpenForm';

function NewExpenses(props) {
	const [btnOpen, setButtonOpen] = useState(false);

	const saveExpenseComponentData = (enteredExpenseData) => {
		const expenseData = {
			...enteredExpenseData,
			id: Math.random(),
		};
		props.onAddExpense(expenseData);
	};

	const clickHandler = () => {
		setButtonOpen(true);
	};

	let showForm = '';

	if (btnOpen) {
		showForm = <ExpensesForm onSaveExpenseData={saveExpenseComponentData} />;
	}

	return (
		<div className="new-expense">
			{/* <button onClick={clickHandler}>Add new car</button> */}
			{showForm}
		</div>
	);
}

export default NewExpenses;
