import React, { useState } from 'react';
import ExpenseRender from '../src/components/Expenses/ExpenseRender';
import NewExpenses from '../src/components/NewExpenses/NewExpenses';

const INITIAL_EXPENSES = [
	{
		title: 'Tesla Model X 2022',
		amount: Number(450.0).toFixed(3),
		date: new Date(2022, 3, 25),
	},
	{
		title: 'Range Rover Vogue 2022',
		amount: Number(650.0).toFixed(3),
		date: new Date(2023, 4, 5),
	},
	{
		title: 'Mercedes-Benz S Coupe 2018',
		amount: Number(250.0).toFixed(3),
		date: new Date(2022, 2, 22),
	},
	{
		title: 'Mercedes-Benz G65 2023',
		amount: Number(850.0).toFixed(3),
		date: new Date(2025, 7, 12),
	},
];

function App() {
	const [expenses, setExpenses] = useState(INITIAL_EXPENSES);
	console.log(setExpenses);
	const addExpenseHandler = (expense) => {
		setExpenses((prevExpenses) => {
			const newExp = [expense, ...prevExpenses];
			return newExp;
		});
	};

	return (
		<div>
			<NewExpenses onAddExpense={addExpenseHandler} />
			<ExpenseRender items={expenses} />
		</div>
	);
}

export default App;
