import React from 'react';
import ExpenseItem from './ExpenseItem';
import './ExpenseRender.css';
import './ExpensesFilter';
import Card from '../UI/Card';
import ExpensesFilter from './ExpensesFilter';
import { useState } from 'react';

function ExpenseRender(props) {
	const [filteredYear, setFilteredYear] = useState('2020'); //* We initiallized a year

	const filterChangeHandler = (selectedYear) => {
		setFilteredYear(selectedYear);
	};

	// const years = props.years.filter((year) => {
	// 	return year === +filteredYear;
	// }); //We got an Array

	// const years = props.items.date.getFullYear().filter((year) => {
	// 	return year === +filteredYear;
	// }); //We got an Array

	const years = props.items
		.map((exp) => exp.date.getFullYear())
		.filter((year) => {
			return year === +filteredYear;
		});
	console.log(years);

	return (
		<div>
			<Card className="expenses">
				<ExpensesFilter
					currentYear={filteredYear}
					onChangeFilter={filterChangeHandler}
					// onChangeFilter={setFilteredYear(years)}
				/>
				<h2>Sultan's cars:</h2>
				{props.items.map((expenses) => {
					console.log(expenses.date.getFullYear());
					return (
						<ExpenseItem
							key={expenses.id}
							title={expenses.title}
							amount={expenses.amount}
							date={expenses.date}
						/>
					);
				})}
			</Card>
		</div>
	);
}

export default ExpenseRender;

